function newItem() {
  let t = $('<li></li>'),
    n = $('#input').val();
  t.append(n),
    '' === n ? alert('You must write something!') : $('#list').append(t),
    t.on('dblclick', function () {
      t.toggleClass('strike');
    });
  let e = $('<crossOutButton></crossOutButton>');
  e.append('X'),
    t.append(e),
    e.on('click', function () {
      $(this).closest('li').remove();
    }),
    $('#list').sortable();
}
$('#button').on('click', function (t) {
  t.preventDefault();
});

